import { ChangeEvent, useEffect, useState } from "react"
import { optionType } from "../types"
import Search from "./components/Search"
const App = (): JSX.Element => {


 const [term, setTerm] = useState<string>('')
 const [options, setOptions] = useState<[]>([])
const [city, setCity] = useState<optionType | null >(null)
const [forecast, setForecast] = useState<forecastType | null>(null)

 const getSearchOptions = (value: string) => {
  fetch(`https://api.openweathermap.org/geo/1.0/direct?q=${value.trim()}&limit=5&appid=${process.env.REACT_APP_API_KEY}`)
 .then((res)=>res.json())
 .then((data) => setOptions(data))
}

const onInputChange = (ev: ChangeEvent<HTMLInputElement>) => {
  const value = ev.target.value.trim();
  setTerm(value);

  if(value === '') return 

  getSearchOptions(value)
 }

 const getForecast = (city: optionType) => {
fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city.lat}&lon=${city.lon}&units=metric&appid=${process.env.REACT_APP_API_KEY}`)
.then((res)=>res.json())
.then((data) => setForecast((data)))

 }

 const onSubmit = () => {
if(!city)  return
getForecast(city)


 }

 const onOptionSelect = (option: optionType) => {
  setCity(option);


fetch(`https://api.openweathermap.org/data/2.5/weather?lat=${option.lat}&lon=${option.lon}&units=metric&appid=${process.env.REACT_APP_API_KEY}`)
.then((res)=>res.json())
.then((data) => console.log({data}))
 }

 useEffect(() => {
  if(city) {
    setTerm(city.name)
    setOptions([])
  }

 }, [city])
  return (
    <main className="flex 
    justify-center 
    items-center 
    bg-gradient-to-br 
    from-sky-400 
    via-rose-400 
    to-lime-400 
    h-[100vh] 
    w-full">
{forecast ? (
  'we have a forecast'
) : (
<Search term={term} options={options} onInputChange={onInputChange} onOptionSelect={onOptionSelect} onSubmit={onSubmit}/>
)}
    </main>
  )
}

export default App
